import { createApp } from '/modules/app.mjs'
import { loadConfigJson } from '/modules/integration-config.mjs'
import { controls, libraries, mocks } from '/modules/demo.config.mjs'

(async function () {
  const config = await loadConfigJson()
  const settingsObject = {
    name: config.id
  }
  window._loIntegrationSettings = [settingsObject]
  createApp({
    controls,
    libraries,
    mocks,
    name: config.id,
    settings: settingsObject,
    settingsSchema: config.settingsSchema
  })
})()
