const getConfig = require('../utils/get-config')
const globalState = require('../utils/global-state')

module.exports = (program) => {
  // Add CLI options
  program.option('-c, --config [path]', 'Path to JSON config file', 'lucky.json')
  program.parse(process.argv)

  const globals = {
    // Properties
    config: getConfig(program),
    globalState: globalState.getGlobalState(),

    // Methods
    getGlobalState: globalState.getGlobalState,
    setGlobalState: globalState.setGlobalState
  }

  return globals
}
