#!/usr/bin/env node

const program = require('commander')
const pkgJson = require('../package.json')

program
  .version(pkgJson.version)
  .command('redis-cluster', 'generate a Kubernetes redis cluster')
  .command('docs', 'generate reference docs for current directory')
  .command('start', 'automatically connect to kubernetes services before running command')
  .command('build', 'generate deployment resources')
  .command('create', 'scaffold out zero-config repos')
  .parse(process.argv)
