"use strict";
const fs = require('fs-extra');
const path = require('path');
const merge = require('lodash/merge');
// Options
const globalDir = require('os').homedir();
const globalStatePath = path.resolve(globalDir, '.lucky.json');
const API = {
    getGlobalState() {
        return fs.readJsonSync(globalStatePath, { throws: false }) || {};
    },
    setGlobalState(stateToMerge) {
        const currentState = API.getGlobalState();
        const newState = merge(currentState, stateToMerge);
        return fs.writeJsonSync(globalStatePath, newState);
    }
};
module.exports = API;
